package day2;

public class StringFunction {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String k="Welcome";
System.out.println(k.equals("Welcome"));
System.out.println(k.length());
System.out.println(k.charAt(5));
System.out.println(k.isEmpty());
System.out.println(k.toUpperCase());
System.out.println(k.toLowerCase());
System.out.println(k.concat("to java"));
System.out.println(k.replace('w','W'));
System.out.println(k.substring(3,5));
System.out.println("Welcome".substring(3,5));
	}

}
